
'use strict';

const appLog = require('chqportal/logger.js');


function get(key) {
	if (process.type == "browser") {
		return global[key];
	} else {
		return require("remote").getGlobal(key);
	}
}

function set(key, value) {
	appLog.info(key,value,process.type)
	if (process.type == "browser") {
		global[key] = value;
		console.log(key,obj,value);
	} else {
		var obj = require("remote").getGlobal(key);
		console.log(key,obj,value);
		obj = value;
	}
}


module.exports.get = get;
module.exports.set = set;
