
'use strict';
const async = require('async');

const setting = require('./setting.js');
const userSetting = require('./userSetting.js');

const appLog = require('./logger.js');

module.exports.setting = setting;
module.exports.userSetting = userSetting;


module.exports.init = function(cb) {
	async.parallel([setting.init,userSetting.init],function(err,data) {
		cb(err,{setting:data[0],userSetting:data[1]})
	})
}



