// init();

function setTheme(theme) {
	nconf.set("ui:theme",theme);
	nconf.save();
}

function getTheme() {
	return nconf.get("ui:theme");
}

function setFullScreen(bool) {
	nconf.set("ui:fullScreen",bool);
	nconf.save();
}

function getFullScreen() {
	return nconf.get("ui:fullScreen");
}

function get(key) {
	return nconf.get(key);
}

function set(key,value) {
	nconf.set(key,value);
	nconf.save();
}

module.exports.setTheme = setTheme;
module.exports.getTheme = getTheme;
module.exports.setFullScreen = setFullScreen;
module.exports.getFullScreen = getFullScreen;
module.exports.get = get;
module.exports.set = set;

